function icoView($r, $text)
{
	$label = $text;
	global $viewItemPage;
	vprintf('<a href="%s" class="%s" title="%s"><span>%s</span></a>',array(
		"javascript:self.location='?mod=".LURL::dotPage($viewItemPage)."&id=".$_REQUEST["id"]."&itemid=".$r->ID."'",                                                      
		'ico view',
		$label,
		$label
	));
}

function icoEdit($a,$r)
{
	global $editItemPage;
	$label = 'แก้ไข';
	vprintf('<a href="%s" class="%s" title="%s"><span>%s</span></a>',array(
		"javascript:self.location='?mod=".LURL::dotPage($editItemPage)."&id=".$_REQUEST["id"]."&itemid=".$r->ID."'",
		'ico edit',
		$label,
		$label
	));
}

